mapper.insert:
insert into orders(number, status, user_id, user_name, address_book_id, order_time, checkout_time, pay_method,
                   pay_status, amount, remark, phone, address,
                   consignee, estimated_delivery_time, delivery_status, pack_amount, tableware_number,
                   tableware_status)
values ( #{number}, #{status}, #{userId}, #{userName}, #{addressBookId}, #{orderTime}, #{checkoutTime}
       , #{payMethod}, #{payStatus}, #{amount}
       , #{remark}, #{phone}, #{address}, #{consignee}, #{estimatedDeliveryTime}, #{deliveryStatus}
       , #{packAmount}, #{tablewareNumber}, #{tablewareStatus})

mapper.update:
update orders
cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, pay_status = #{payStatus}, pay_method = #{payMethod, checkout_time = #{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
select count(*) from orders


        status = #{status}


        and order_time >=#{beginTime}


        and order_time <= #{endTime}

select od.name, sum(od.name) number
from orders o,
     order_detail od
where od.order_id = o.id
  and o.status = #{status}
  and o.order_time >= #{beginTime}
  and o.order_time <= #{endTime}
group by od.name
order by number limit 0,10
